uniform sampler2D ol;uniform float il;uniform vec3 lo;uniform vec4 ls;uniform vec4 oo;uniform vec4 mo;uniform vec3 im;uniform bool drawPath;varying vec4 gd;varying vec3 gg;varying vec3 hh;void main(){float a=il*0.01;gl_FragColor.rgb=gd.rgb;gl_FragColor.a=1.0;if(!drawPath){
#ifdef GLOBAL
gl_FragColor=(texture2D(ol,gl_PointCoord)*gl_FragColor);if(gl_FragColor.r>=0.15&&gl_FragColor.g>=0.15&&gl_FragColor.b>=0.15){gl_FragColor.rgb-=vec3(0.15,0.15,0.15);}
#else
gl_FragColor=(texture2D(ol,gl_PointCoord)*gl_FragColor)+vec4(0.27,0.27,0.27,0.0);
#endif
gl_FragColor.a*=a;}else{gl_FragColor.a*=(0.3*a);}if(gl_FragColor.r<=1e-6&&gl_FragColor.g<=1e-6&&gl_FragColor.b<=1e-6){gl_FragColor.rgb=vec3(0.1,0.1,0.1);gl_FragColor.a=0.1;}}

attribute vec3 $auxpos1;uniform mat4 ss;uniform mat4 ii;uniform sampler2D pi;uniform vec2 le;uniform sampler2D se;uniform vec2 sl;uniform float me;uniform float el;uniform float es;uniform vec2 pm;uniform bool drawPath;uniform sampler2D om;uniform bool po;varying vec4 gd;varying vec3 gg;varying vec3 hh; $linearInterpolator  $lonlat2position  $localTrans  $curveInter void main(void){float a=fract($auxpos1.x*2.0/sl.x);float b=floor($auxpos1.x*2.0/sl.y)/sl.y;vec4 c=texture2D(se,vec2(a,b));if(abs(c.r-$auxpos1.x)<Epsilon){vec3 d=c.gba;a=fract(($auxpos1.x*2.0+1.0)/sl.x);b=floor(($auxpos1.x*2.0+1.0)/sl.y)/sl.y;vec4 e=texture2D(se,vec2(a,b));vec3 f=e.gba;vec3 g,h,i,j;float k,l;float m=1.0;float n=0.0;
#ifdef GLOBAL
d=lonlat2position(d);f=lonlat2position(f);g=(d+f)*0.5;h=normalize(g);i=d-f;k=length(i);g=h*(length(g)+k*0.6);if(k<=1000.0){l=32.0;}else if(k<=10000.0){l=24.0;}else if(k<=500000.0){l=18.0;}else if(k<=1000000.0){l=40.0;}else{l=floor(k*0.00001);}if(drawPath){n=$auxpos1.z;}else{n=mod($auxpos1.z+(l*2.0+1.0)/me*es,l*2.0+1.0);}if((n>=l)&&(n<(l*2.0+1.0))){m=-1.0;n-=l;}vec3 o=normalize(i);vec3 p=g+o*(k*0.6*m);float q=n/l;q=clamp(q,0.0,1.0);if(m<=0.0){j=getPoint(q,g,p,f,f);}else{j=getPoint(q,d,d,p,g);}gg=normalize(j);
#else
d=wgs84ToWebMerc(d);f=wgs84ToWebMerc(f);g=(d+f)*0.5;h=vec3(0.0,0.0,1.0);i=d-f;k=length(i);g=g+h*(k*0.3);if(k<=1000.0){l=48.0;}else if(k<=10000.0){l=42.0;}else if(k<=100000.0){l=32.0;}else if(k<=1000000.0){l=24.0;}else if(k<=2000000.0){l=36.0;}else{l=floor(k*0.000006);}if(drawPath){n=$auxpos1.z;}else{n=mod($auxpos1.z+(l*2.0+1.0)/me*es,l*2.0+1.0);}if((n>=l)&&(n<(l*2.0+1.0))){m=-1.0;n-=l;}vec3 o=normalize(i);vec3 p=g+o*(k*0.3*m);float q=n/l;q=clamp(q,0.0,1.0);if(m<=0.0){j=getPoint(q,g,p,f,f);}else{j=getPoint(q,d,d,p,g);}gg=vec3(0.0,0.0,1.0);
#endif
gl_Position=ss*ii*vec4(j,1.0);hh=j;a=fract($auxpos1.y/le.x);b=floor($auxpos1.y/le.y)/le.y;float r=(texture2D(pi,vec2(a,b))).r;float s=getLinearValue(pm,r);if(!drawPath){gl_PointSize=el*pow(2.718,s);}gd=texture2D(om,vec2(s,0.5));}else{vec3 j=vec3(0.0,0.0,0.0);gl_Position=ss*ii*vec4(j,1.0);hh=j;if(!drawPath) gl_PointSize=el;gd=vec4(1.0,1.0,0.0,1.0);gg=normalize(j);}}
