uniform vec3 pp;uniform vec4 ss;uniform vec4 ls;uniform vec4 sl;uniform vec3 oe;uniform float ms;uniform vec3 pl;varying vec2 gg;varying vec3 hd;varying vec3 dc;varying vec4 dh;const vec3 a=vec3(1.0,1.0,1.0);const vec3 b=vec3(1.0,1.0,1.0);const vec3 c=vec3(0.4,0.4,0.4);void main(){vec3 d=normalize(dc-oe);vec3 e=normalize(hd);vec3 f=normalize(reflect(d,e));float g=max(dot(f,pp),.001);vec3 h=c*sl.rgb*sl.w*pow(g,16.0);vec3 i=a*ls.rgb*ls.w*clamp(dot(e,pp),.0,1.0);vec3 j=b*ss.rgb*ss.w;vec3 k=vec3(j+i+h);float l=abs(gg.x-0.5)/0.5;gl_FragColor=(1.0-l)*vec4(pl,1.0)+l*dh;gl_FragColor*=vec4(k,1.0);float m=ms*0.01;gl_FragColor.w=m;}

attribute vec3 $position;attribute vec3 $auxpos1;uniform mat4 os;uniform mat4 ee;uniform bool io;uniform float om;uniform sampler2D mp;uniform sampler2D ps;uniform vec2 mi;uniform float si;uniform vec2 mo;uniform float ii;uniform vec2 lp;uniform bool lo;uniform sampler2D so;varying vec3 hd;varying vec3 dc;varying vec2 gg;varying vec4 dh; $lonlat2position  $translationMat  $linearInterpolator  $localTrans  $expoEaseOut const float a=0.0;const float b=1.0;const float c=2.0;const float d=3.0;void main(void){float e=fract($auxpos1.x/mi.x);float f=floor($auxpos1.x/mi.y)/mi.y;float g=(texture2D(mp,vec2(e,f))).r;float h=(texture2D(ps,vec2(e,f))).r;float i=mod(ii,si);float j=getExpoEaseOutValue(i,g,h,si);if(lo) j=h;float k=getLinearValue(lp,j);if(io){float l=200000.0;if($auxpos1.y==a||$auxpos1.y==d){l=getScope(mo,k);}else if($auxpos1.y==b||$auxpos1.y==c){l=1.25*getScope(mo,k);}if($auxpos1.y==a) gg=vec2(0.0,0.0);else if($auxpos1.y==d) gg=vec2(0.0,1.0);else if($auxpos1.y==b) gg=vec2(1.0,0.0);else if($auxpos1.y==c) gg=vec2(1.0,1.0);float m=$auxpos1.z*2.0*PI;vec3 n=vec3(l*cos(m),l*sin(m),0.0);vec3 o,p;mat4 q=mat4(1.0);
#ifdef GLOBAL
mat4 r=getTransMat($position);q=toRotationMat(r);o=(r*vec4(n,1.0)).xyz;vec4 s=vec4(0.0,0.0,1.0,0.0);p=(q*s).xyz;
#else
o=wgs84ToWebMerc($position);o+=n;p=vec3(1.0,0.0,1.0);
#endif
gl_Position=os*ee*vec4(o,1.0);hd=normalize(p);dc=o;dh=texture2D(so,vec2(k,0.5));}else{float t=getScope(mo,k);mat4 r=getTransMat($position);float u=($auxpos1.y-om/2.0)*(20.0/om);float v=($auxpos1.z-om/2.0)*(20.0/om);float w=exp(-0.0831*(u*u+v*v));float x=atan(u,v);u=w*cos(2.0*x);v=w*sin(2.0*x);float y=sqrt(u*u+v*v)*getScope(mo,k);vec3 o=(r*vec4(t*($auxpos1.y/om-0.5),t*($auxpos1.z/om-0.5),y,1.0)).xyz;vec3 p=normalize(o);gl_Position=os*ee*vec4(o,1.0);hd=p;dc=o;gg=$auxpos1.yz/(om-1.0);}}
